mapper.update:
update gssusertab 
set email=#{email},
	userpassword=#{userPassword},
	userphone=#{userPhone},
	username=#{userName}
where gssuserid=#{gssuserId}

mapper.select:
select count(*)
from gssusertab 
where email=#{email}

select count(*)
from gssusertab 
where gssuserid=#{userID}

select count(*)
from gssusertab 
where userphone=#{phoneNumber}
